addlayer('ly_slide_next_btn'); set(layer[ly_slide_next_btn], edge='leftbottom', align='leftbottom', y=50, x=80, type='text', width=65, html='Next', css='padding: 1rem; color:#fff; text-align: center;', bg=true, bgcolor='0x000000', bgalpha='0.8', onclick='help_slide_next()', );
addlayer('ly_slide_show_90_deg_with_init_btn'); set(layer[ly_slide_show_90_deg_with_init_btn], edge='leftbottom', align='leftbottom', y=50, x=150, type='text', html='Init (90,20)', css='padding: 1rem; color:#fff; text-align: center;', bg=true, bgcolor='0x000000', bgalpha='0.8', onclick='help_slide_init(0, 90, 20)', );
