addlayer('ly_slide_show_add_20_deg_btn'); set(layer[ly_slide_show_add_20_deg_btn], edge='leftbottom', align='leftbottom', y=110, x=150, type='text', html='Show (+20 ath)', css='padding: 1rem; color:#fff; text-align: center;', bg=true, bgcolor='0x000000', bgalpha='0.8', onclick='help_slide_show(get(help_slide_settings.slide_current_index), calc(hotspot[hs_slide_0].ath+20), get(hotspot[hs_slide_0].atv))', );
addlayer('ly_slide_init_btn'); set(layer[ly_slide_init_btn], edge='leftbottom', align='leftbottom', y=170, x=10, type='text', html='Init Slide', css='padding: 1rem; color:#fff; text-align: center;', bg=true, bgcolor='0x000000', bgalpha='0.8', onclick='help_slide_init()', );
addlayer('ly_slide_show_specific_index_btn'); set(layer[ly_slide_show_specific_index_btn], edge='leftbottom', align='leftbottom', y=170, x=100, type='text', html='Show Specific Index Slide', css='padding: 1rem; color:#fff; text-align: center;', bg=true, bgcolor='0x000000', bgalpha='0.8', onclick='help_slide_show_specific_index()', );


const startIdx = krpano.get('help_slide_settings.slide_start_index');
const endIdx = krpano.get('help_slide_settings.slide_end_index');
let slideIndex = prompt(`보여주길 원하는 슬라이드 index를 입력하세요 ${startIdx} ~ ${endIdx}`);
var regex = /^[0-9]+$/; // number only

if(slideIndex){
    if(regex.test(slideIndex)){

        if( slideIndex >= startIdx && slideIndex <= endIdx ){
            krpano.call( `help_slide_show(${slideIndex})` );
        }else{
            alert('범위내 숫자만 입력해주세요.');
            krpano.call('help_slide_show_specific_index()');
        }
    }else{
        alert('숫자만 입력해주세요.');
        krpano.call('help_slide_show_specific_index()');
    }
}
